import { React, useContext, useState } from "react";
import AuthContext from "../Context/Auth.jsx";
import {
  BackgroundDiv,
  DivMeioTela,
  DivNavLink,
  DivSuperior,
  DivTitulo,
  ItemCarrinho,
  SubTitulo,
  TextoBranco,
  TiraAzul,
  Titulo,
} from "./estilos.js";

export const Carrinho = () => {
  const userCredenciais = useContext(AuthContext);
  const [carrinho, setCarrinho] = useState(userCredenciais.user.pedidos);

  const userLogado = userCredenciais.user.email ? true : false;
  function somarQuantidade(item, i) {
    let quantidade = item.qt
    let novoItem = {...item, qt: quantidade + 1};
    setCarrinho(carrinho.splice(i, 1));
    setCarrinho([...carrinho, novoItem]);
  }

  console.log(carrinho);

  return (
    <BackgroundDiv>
      <DivSuperior>
        <DivTitulo>
          <DivNavLink to="/">
            <Titulo>{"Porco Rosso"}</Titulo>
          </DivNavLink>
          <SubTitulo>{"Pizzaria"}</SubTitulo>
        </DivTitulo>
      </DivSuperior>
      <TiraAzul>
        <DivNavLink to="/Menu/Menu">
          <TextoBranco>{"menu"}</TextoBranco>
        </DivNavLink>
        <DivNavLink to="/SobreNos/SobreNos">
          <TextoBranco>{"sobre nós"}</TextoBranco>
        </DivNavLink>
      </TiraAzul>
      {userLogado ? (
        <>
          <DivMeioTela>
            {carrinho.map((item, i) => {
              return (
                <ItemCarrinho key={i} id={item} onClick={() => {somarQuantidade(item, i)}}>
                  {item.nome}
                  {'\n'}
                  {item.qt}
                </ItemCarrinho >
              );
            })}
          </DivMeioTela>
        </>
      ) : (
        <DivMeioTela />
      )}
    </BackgroundDiv>
  );
};
