import { React, useContext, useState } from "react";
import { Navigate } from "react-router-dom";
import AuthContext from "../Context/Auth.jsx";
import {
  BackgroundDiv,
  Botao,
  Caixa,
  DivMeioAzul,
  DivMeioRosa,
  DivMeioTela,
  DivNavLink,
  DivSuperior,
  DivTitulo,
  SubTitulo,
  TextoAzul,
  TextoBemVindo,
  TextoBranco,
  TextoEntrarBranco,
  TextoRosaBranco,
  TiraAzul,
  Titulo,
} from "./estilos.js";

export const Login = () => {
  const userCredenciais = useContext(AuthContext);
  const [user, setUser] = useState({ email: "", senha: "" });

  function fazerLogin() {
    userCredenciais.setUser(user);
  }

  if(userCredenciais.email) {
    return <Navigate replace to="/login" />;
  }

  return (
    <BackgroundDiv>
      <DivSuperior>
        <DivTitulo>
          <DivNavLink to="/">
            <Titulo>{"Porco Rosso"}</Titulo>
          </DivNavLink>
          <SubTitulo>{"Pizzaria"}</SubTitulo>
        </DivTitulo>
      </DivSuperior>
      <TiraAzul>
        <DivNavLink to="/Menu/Menu">
          <TextoBranco>{"menu"}</TextoBranco>
        </DivNavLink>
        <DivNavLink to="/SobreNos/SobreNos">
          <TextoBranco>{"sobre nós"}</TextoBranco>
        </DivNavLink>
      </TiraAzul>
      <DivMeioTela>
        <DivMeioAzul>
          <TextoEntrarBranco>{"Entrar:"}</TextoEntrarBranco>
          <TextoBemVindo>{"Bem-vindo(a) de volta!"}</TextoBemVindo>
        </DivMeioAzul>
        <DivMeioRosa>
          <TextoRosaBranco>{"Email"}</TextoRosaBranco>
          <Caixa
            placeholder="Insira seu email aqui..."
            value={user.email}
            onChange={(e) => {
              setUser({ ...user, email: e.target.value });
            }}
          />
          <TextoRosaBranco>{"Senha"}</TextoRosaBranco>
          <Caixa
            placeholder="Insira sua senha..."
            type="password"
            value={user.senha}
            onChange={(e) => {
              setUser({ ...user, senha: e.target.value });
            }}
          />
          <Botao variant="contained" onClick={() => {fazerLogin()}}>
            {"Entrar"}
          </Botao>
          <DivNavLink to="/Cadastrar/Cadastrar">
            <TextoAzul>{"Criar uma conta"}</TextoAzul>
          </DivNavLink>
        </DivMeioRosa>
      </DivMeioTela>
    </BackgroundDiv>
  );
};
