import AuthContext from "../Context/Auth.jsx";
import {
  BackgroundDiv,
  DivMeioTela,
  DivNavLink,
  DivSuperior,
  DivTitulo,
  ItemCarrinho,
  SubTitulo,
  TextoBranco,
  TiraAzul,
  Titulo,
} from "./estilos.js";

export const Carrinho = () => {
  const userCredenciais = useContext(AuthContext);

  useEffect(() => {
