    };
  }, [input]);
  const carrinho = userCredenciais.user.pedidos;

  const userLogado = userCredenciais.user.email ? true : false;

  return (
    <BackgroundDiv>
      <DivSuperior>
        <DivTitulo>
          <DivNavLink to="/">
            <Titulo>{"Porco Rosso"}</Titulo>
          </DivNavLink>
          <SubTitulo>{"Pizzaria"}</SubTitulo>
        </DivTitulo>
      </DivSuperior>
      <TiraAzul>
        <DivNavLink to="/Menu/Menu">
          <TextoBranco>{"menu"}</TextoBranco>
        </DivNavLink>
        <DivNavLink to="/SobreNos/SobreNos">
          <TextoBranco>{"sobre nós"}</TextoBranco>
        </DivNavLink>
      </TiraAzul>
      {userLogado ? (
        <>
          <DivMeioTela>
            {carrinho.forEach((item) => {
              <ItemCarrinho>{item.nome}</ItemCarrinho>;
            })}
          </DivMeioTela>
        </>
      ) : (
        <DivMeioTela />
      )}
    </BackgroundDiv>
  );
};
