import styled from "styled-components";

export const BackgroundDiv = styled.div`
  backgorund: #fff4ea;
`;

export const TiraAzul = styled.div`
  background: #91bdff;
  display: flex;
  flex-direction: row;
  justify-content: space-evenly;
  align-items: center;
  height: 84px;
`;

export const DivTitulo = styled.div`
  display: flex;
  flex-direction: row;
  align-items: flex-end;
  gap: 15px;
`;

export const Titulo = styled.h1`
  height: 69px;
  font-family: "Titan One";
  font-style: normal;
  font-weight: 400;
  font-size: 64px;
  color: #90bdff;
  text-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
`;

export const SubTitulo = styled.p`
  width: 277px;
  height: 48px;
  font-family: "Titan One";
  font-style: normal;
  font-weight: 400;
  font-size: 32px;
  color: #435ebd;
  text-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
`;

export const TextoBranco = styled.p`
  height: 42px;
  font-family: "Titan One";
  font-style: normal;
  font-weight: 400;
  font-size: 36px;
  line-height: 41px;
  color: #fff4ea;
  text-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
`;
