import styled from "styled-components";

export const BackgroundDiv = styled.div`
  color: #fff4ea;
`;

export const TiraAzul = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-evenly;
  backgorundcolor: #90bdff;
  height: 84px;
`;

export const Titulo = styled.h1`
  width: 497px;
  height: 69px;
  font-family: "Titan One";
  font-style: normal;
  font-weight: 400;
  font-size: 64px;
  line-height: 73px;
  color: #90bdff;
  text-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
`;

export const TextoBranco = styled.p`
  height: 42px;
  font-family: "Titan One";
  font-style: normal;
  font-weight: 400;
  font-size: 36px;
  line-height: 41px;
  color: #fff4ea;
  text-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
`;
